$page_count = 3;
//从correct中选择对应的报告
$select = mysql_query("select * from correct where  tid='$uid'");
//报告总数
$RecordCount = mysql_num_rows($select);
//  echo $RecordCount;
$PageCount = ceil($RecordCount / $page_count);
//  echo $PageCount;
$offect = ($page - 1) * $page_count;
// echo $offect;
$sql = mysql_query("select * from correct where   tid='$uid' order by assignmentid desc limit $offect,$page_count");

?>

